    public static ArrayList<Sekolah> getListnya(){
        ArrayList<Sekolah> list = new ArrayList<>();
        for (int position = 0; position < NamaSekolah.length; position++){
            Sekolah sekolah = new Sekolah();
            sekolah.setName(NamaSekolah[position]);
            sekolah.setDetail(detail[position]);
            sekolah.setPhoto(gambarsekul[position]);
            list.add(sekolah);
        }
        return list;
    }
